import {useEffect, useContext} from 'components/node_modules/react'

import useFetch from 'components/node_modules/hooks/useFetch'
import {CurrentUserContext} from 'components/node_modules/contexts/currentUser'
import useLocalStorage from 'components/node_modules/hooks/useLocalStorage'

const CurrentUserChecker = ({children}) => {
  const [, dispatch] = useContext(CurrentUserContext)
  const [{response}, doFetch] = useFetch('/user')
  const [token] = useLocalStorage('token')

  useEffect(() => {
    if (!token) {
      dispatch({type: 'SET_UNAUTHORIZED'})
      return
    }

    doFetch()
    dispatch({type: 'LOADING'})
  }, [doFetch, dispatch, token])

  useEffect(() => {
    if (!response) {
      return
    }

    dispatch({type: 'SET_AUTHORIZED', payload: response.user})
  }, [response, dispatch])
  return children
}

export default CurrentUserChecker
