import React, {useEffect, Fragment} from 'pages/globalFeed/node_modules/react'
import {stringify} from 'pages/globalFeed/node_modules/query-string'

import Feed from 'pages/globalFeed/node_modules/components/feed'
import useFetch from 'pages/globalFeed/node_modules/hooks/useFetch'
import Pagination from 'pages/globalFeed/node_modules/components/pagination'
import {getPaginator, limit} from 'pages/globalFeed/node_modules/utils'
import PopularTags from 'pages/globalFeed/node_modules/components/popularTags'
import Loading from 'pages/globalFeed/node_modules/components/loading'
import ErrorMessage from 'pages/globalFeed/node_modules/components/errorMessage'
import FeedToggler from 'pages/globalFeed/node_modules/components/feedToggler'
import Banner from 'pages/globalFeed/node_modules/components/banner'

const GlobalFeed = ({location, match}) => {
  const {offset, currentPage} = getPaginator(location.search)
  const stringifiedParams = stringify({
    limit,
    offset
  })
  const apiUrl = `/articles?${stringifiedParams}`
  const currentUrl = match.url
  const [{response, error, isLoading}, doFetch] = useFetch(apiUrl)

  useEffect(() => {
    doFetch()
  }, [currentPage, doFetch])

  return (
    <div className="home-page">
      <Banner />
      <div className="container page">
        <div className="row">
          <div className="col-md-9">
            <FeedToggler />
            {isLoading && <Loading />}
            {error && <ErrorMessage />}
            {!isLoading && response && (
              <Fragment>
                <Feed articles={response.articles} />
                <Pagination
                  total={response.articlesCount}
                  limit={limit}
                  url={currentUrl}
                  currentPage={currentPage}
                />
              </Fragment>
            )}
          </div>
          <div className="col-md-3">
            <PopularTags />
          </div>
        </div>
      </div>
    </div>
  )
}

export default GlobalFeed
